(6, 'Kontakt', '<body>\r\n<main>\r\n    <div class=\"container\">\r\n        <h2>Skontaktuj się z nami</h2>\r\n        <form action=\"mailto:example@domain.com\" method=\"POST\" enctype=\"text/plain\">\r\n            <label for=\"name\">Imię:</label>\r\n            <input type=\"text\" id=\"name\" name=\"name\" required><br><br>\r\n            <label for=\"email\">Email:</label>\r\n            <input type=\"email\" id=\"email\" name=\"email\" required><br><br>\r\n            <label for=\"message\">Wiadomość:</label><br>\r\n            <textarea id=\"message\" name=\"message\" rows=\"5\" required></textarea><br><br>\r\n            <input type=\"submit\" value=\"Wyślij\">\r\n        </form>\r\n    </div>\r\n</main>\r\n</body>\r\n', 1),
(7, 'poligon', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Loty Kosmiczne</title>\r\n    <script src=\"assets/jquery-3.7.1.min.js\"></script>\r\n    <script src=\"js/kolorujtlo.js\" type=\"text/javascript\"></script>\r\n    <script src=\"js/timedate.js\" type=\"text/javascript\"></script>\r\n    <link rel=\"stylesheet\" href=\"../css/style2.css\">\r\n\r\n    <script src=\"https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js\"></script>\r\n\r\n    <script>\r\n        // Ładowanie index.php przed załadowaniem reszty strony\r\n        $(document).ready(function() {\r\n            $.get(\'index.php\', function(data) {\r\n                // Wyciągamy tylko sekcję head z index.php\r\n                var headContent = $(data).filter(\'head\').html();\r\n                // Dodajemy zawartość head z index.php do naszego dokumentu\r\n                $(\'head\').append(headContent);\r\n            });\r\n        });\r\n\r\n    </script>\r\n</head>\r\n<body onload=\"startclock()\">\r\n\r\n    <!-- Zegar i data na stronie -->\r\n    <div class=\"time-container\">\r\n        <div id=\"zegarek\">19:33</div>\r\n        <div id=\"data\">15.01.2025</div>\r\n    </div>\r\n\r\n    <!-- Popup na otwarciu strony -->\r\n    <script>\r\n        $(document).ready(function () {\r\n            alert(\"TO JEST POLIGON\");\r\n        });\r\n    </script>\r\n\r\n    <!-- Zmiana koloru tła -->\r\n    <form method=\"post\" name=\"background\" class=\"color-buttons\">\r\n        <input type=\"button\" value=\"Żółty\" onclick=\"changeBackground(\'#FFF000\')\" class=\"color-btn\">\r\n        <input type=\"button\" value=\"Czarny\" onclick=\"changeBackground(\'#000000\')\" class=\"color-btn\">\r\n        <input type=\"button\" value=\"Biały\" onclick=\"changeBackground(\'#ffffff\')\" class=\"color-btn\">\r\n        <input type=\"button\" value=\"Zielony\" onclick=\"changeBackground(\'#00ff00\')\" class=\"color-btn\">\r\n        <input type=\"button\" value=\"Niebieski\" onclick=\"changeBackground(\'#0000ff\')\" class=\"color-btn\">\r\n        <input type=\"button\" value=\"Pomarańczowy\" onclick=\"changeBackground(\'#ff8000\')\" class=\"color-btn\">\r\n        <input type=\"button\" value=\"Szary\" onclick=\"changeBackground(\'#c0c0c0\')\" class=\"color-btn\">\r\n        <input type=\"button\" value=\"Czerwony\" onclick=\"changeBackground(\'#ff0000\')\" class=\"color-btn\">\r\n    </form>\r\n\r\n    <main>\r\n        <div class=\"container\">\r\n\r\n            <!-- Powiększa się po kliknięciu -->\r\n            <div id=\"animacjaTestowa1\" class=\"test-block\">\r\n                <span class=\"block-text\">Kliknij mnie</span>\r\n            </div>\r\n            <script>\r\n                $(\"#animacjaTestowa1\").on(\"click\", function () {\r\n                    $(this).animate({\r\n                        width: \"500px\",\r\n                        opacity: 0.4,\r\n                        fontSize: \"3em\",\r\n                        borderWidth: \"10px\"\r\n                    }, 1500);\r\n                });\r\n            </script>\r\n\r\n            <!-- Powiększa się po najechaniu, zmniejsza po odjechaniu -->\r\n            <div id=\"animacjaTestowa2\" class=\"test-block\">\r\n                <span class=\"block-text\">Najedź na mnie</span>\r\n            </div>\r\n            <script>\r\n                $(\"#animacjaTestowa2\").on({\r\n                    \"mouseover\": function () {\r\n                        $(this).animate({\r\n                            width: 300\r\n                        }, 800);\r\n                    },\r\n                    \"mouseout\": function () {\r\n                        $(this).animate({\r\n                            width: 200\r\n                        }, 800);\r\n                    }\r\n                });\r\n            </script>\r\n\r\n            <!-- Powiększa się z każdym kliknięciem -->\r\n            <div id=\"animacjaTestowa3\" class=\"test-block\">\r\n                <span class=\"block-text\">Kliknij kilka razy</span>\r\n            </div>\r\n            <script>\r\n                $(\"#animacjaTestowa3\").on(\"click\", function () {\r\n                    if (!$(this).is(\":animated\")) {\r\n                        $(this).animate({\r\n                            width: \"+=50\",\r\n                            height: \"+=10\",\r\n                            opacity: \"-=0.1\"\r\n                        }, 500);\r\n                    }\r\n                });\r\n            </script>\r\n        </div>\r\n    </main>\r\n\r\n    <div class=\"container\">\r\n        <h2 class=\"center\">Hej! Witaj na stronie poświęconej lotom w kosmos</h2>\r\n        <p>\r\n           To jest poligon.\r\n        </p>\r\n    </div>\r\n\r\n\r\n    <script>\r\n        // Powiększanie po kliknięciu (toggle)\r\n        document.querySelectorAll(\'.click-zoom\').forEach(element => {\r\n            element.addEventListener(\'click\', function() {\r\n                this.classList.toggle(\'zoomed\');\r\n            });\r\n        });\r\n\r\n        // Powiększanie po najechaniu\r\n        document.querySelectorAll(\'.hover-zoom\').forEach(element => {\r\n            element.addEventListener(\'mouseenter\', function() {\r\n                this.classList.add(\'zoomed\');\r\n            });\r\n            element.addEventListener(\'mouseleave\', function() {\r\n                this.classList.remove(\'zoomed\');\r\n            });\r\n        });\r\n\r\n        // Powiększanie z każdym kliknięciem\r\n        document.querySelectorAll(\'.increment-zoom\').forEach(element => {\r\n            let scale = 1;\r\n            element.addEventListener(\'click\', function() {\r\n                scale += 0.2;\r\n                this.style.transform = `scale(${scale})`;\r\n            });\r\n        });\r\n    </script>\r\n</body>\r\n</html>\r\n', 1);

